import { Box, Button, Input, InputLabel, Typography } from '@mui/material';
import axios from 'axios';
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const UpdateProfile = () => {
  const navigate = useNavigate();
  const [inputs, setInputs] = useState({
    name: "",
    tenthMarkpercentage: "",
    twelethMarkPercentage: "",
    currentCGPA: "",
    resume: null,
    tenthmarkSheet: null,
    twelethMarkSheet: null,
    user: ""
  });
  const handleResumeChange =async (e)=>{
    const file= e.target.files[0];
    setInputs(inputs.resume.file);
  }
  const handleTenthChange =async (e)=>{
    const file= e.target.files[0];
    setInputs(inputs.tenthmarkSheet.file);
  }
  const handleTwelethChange =async (e)=>{
    const file= e.target.files[0];
    setInputs(inputs.twelethMarkSheet.file);
  }

  const labelStyles = { mb: 1, mt: 2, fontSize: '24px', fontWeight: 'bold' };

  const handleChange = (e) => {
    setInputs((prevState) => ({
      ...prevState,
      [e.target.name]: e.target.files ? e.target.files[0] : e.target.value,
    }));
  };

  const sendRequest = async () => {
    const ResumeimageRef = ref(storage, image.name);
    await uploadBytes(imageRef, image);
    const imageUrl = await getDownloadURL(imageRef);

    const formData = new FormData();
    formData.append('name', inputs.name);
    formData.append('tenthMarkpercentage', inputs.tenthMarkpercentage);
    formData.append('twelethMarkPercentage', inputs.twelethMarkPercentage);
    formData.append('user', localStorage.getItem('userId'));
    formData.append('currentCGPA', inputs.currentCGPA);
    formData.append('resume', inputs.resume);
    formData.append('tenthmarkSheet', inputs.tenthmarkSheet);
    formData.append('twelethMarkSheet', inputs.twelethMarkSheet);

    const res = await axios.post('http://localhost:5000/student/updateProfile', formData, {
      headers: {
        'Content-Type': 'multipart/form-data'
      }
    }).catch(err => console.log(err));
    const data = res.data;
    return data;
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(inputs);

    sendRequest().then(data => console.log(data)).then(() => navigate('/studentDrives'));
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <Box border={3} borderColor='linear-gradient(90deg, rgba(2,0,36,1) 0%, rgba(36,36,162,1) 40%, rgba(0,212,255,1) 100%)' borderRadius={5} boxShadow='10px 10px 20px #ccc' padding={3} margin={'auto'} marginTop={3} display='flex' flexDirection={'column'} width={'40%'}>
          <Typography fontWeight={'bold'} padding={3} color='grey' variant='h2' TextAlign={'center'}>
            Student profile
            <InputLabel sx={labelStyles}>Name</InputLabel>
            <input type="text" name="name" onChange={handleChange} value={inputs.name} margin='normal' variant='outlined' />
            <InputLabel sx={labelStyles}>Tenth Mark Percentage</InputLabel>
            <input type="text" name="tenthMarkpercentage" onChange={handleChange} value={inputs.tenthMarkpercentage} margin='normal' variant='outlined' />
            <InputLabel sx={labelStyles}>Twelfth Mark Percentage</InputLabel>
            <input type="text" name="twelethMarkPercentage" onChange={handleChange} value={inputs.twelethMarkPercentage} margin='normal' variant='outlined' />
            <InputLabel sx={labelStyles}>Current CGPA</InputLabel>
            <input type="text" name="currentCGPA" onChange={handleChange} value={inputs.currentCGPA} margin='normal' variant='outlined' />
            <InputLabel sx={labelStyles}>Resume</InputLabel>
            <Input type="file" name="resume" onChange={handleResumeChange} />
            <InputLabel sx={labelStyles}>Tenth Mark Sheet</InputLabel>
            <Input type="file" name="tenthmarkSheet" onChange={handleTenthChange} />
            <InputLabel sx={labelStyles}>Twelfth Mark Sheet</InputLabel>
            <Input type="file" name="twelethMarkSheet" onChange={handleTwelethChange} />
          </Typography>
          <Button sx={{ mt: 2, borderRadius: 4 }} variant='contained' color='warning' type='submit'>Submit</Button>
        </Box>
      </form>
    </div>
  );
};

export default UpdateProfile;
